import React, { useEffect, useState } from 'react';
import './App.css';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom';
import axios from 'axios';
import Nav from './Nav';
import DogList from './DogList';
import DogDetails from './DogDetails';

function App() {
  const [dogs, setDogs] = useState([]);

  useEffect(() => {
    async function fetchDogs() {
      const response = await axios.get('http://localhost:5001/dogs');
      setDogs(response.data);
    }
    fetchDogs();
  }, []);

  return (
    <div className="App">
      <BrowserRouter>
        <Nav dogs={dogs.map(dog => dog.name)} />
        <Switch>
          <Route exact path="/dogs" render={() => <DogList dogs={dogs} />} />
          <Route path="/dogs/:name" render={(routeProps) => {
            const dogName = routeProps.match.params.name;
            const currentDog = dogs.find(dog => dog.name.toLowerCase() === dogName.toLowerCase());
            return <DogDetails {...routeProps} dog={currentDog} />;
          }} />
          <Redirect to="/dogs" />
        </Switch>
      </BrowserRouter>
    </div>
  );
}

export default App;
